import React from "react";

// Icons - font awesome
import { FaTimes } from 'react-icons'

// Styles
import { Wrapper } from "./Task.styles";

const Task = ({ task }) => {
  return (
    <Wrapper>
      <h3>{task.text}</h3>
      <p>{task.day}</p>
    </Wrapper>
  );
};

export default Task;
