import React from "react";

const tasks = [
  {
    id: 1,
    text: "Doctors Appointment",
    day: "May 7th at 6:50pm",
    reminder: true,
  },
  {
    id: 2,
    text: "Meeting at School",
    day: "May 8th at 2:00pm",
    reminder: true,
  },
  {
    id: 3,
    text: "Food Shopping",
    day: "May 8th at 5:00pm",
    reminder: false,
  },
];

const Tasks = () => {
  return (
    <>
      {tasks.map((task) => (
        <h3 key={task.id}>{tasks.text}</h3>
      ))}
    </>
  );
};

export default Tasks;
